import React, { useState } from 'react';
import { useRouter } from 'next/router';
import { createClient } from '@supabase/supabase-js';
import { FaUser, FaLock } from 'react-icons/fa';
import Link from 'next/link';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || '';
const supabase = createClient(supabaseUrl, supabaseKey);

const Login: React.FC = () => {
    const router = useRouter();
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!userId || !password) {
      setError('ユーザーIDとパスワードを入力してください。');
      return;
    }

        try {
      const { data, error } = await supabase.auth.signInWithPassword({
        email: userId,
        password: password,
      });

      if (error) {
        setError('ログインに失敗しました。ユーザーIDまたはパスワードが間違っています。');
      } else {
                router.push('/mainmenu');
      }
    } catch (err:any) {
      console.error("Error during login", err);
      setError('ログイン処理中にエラーが発生しました。');
    }
  };

    return (
        <div className="min-h-screen h-full bg-gray-100 flex items-center justify-center">
            <div className="bg-white p-8 rounded shadow-md w-96">
                <h2 className="text-2xl font-bold text-gray-700 mb-6 text-center">ログイン</h2>
                <form onSubmit={handleLogin}>
                    <div className="relative mb-4">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <FaUser className="text-gray-500" />
                        </div>
                        <input
                            type="text"
                            id="userId"
                            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-blue-500"
                            placeholder="ユーザーID"
                            value={userId}
                            onChange={(e) => setUserId(e.target.value)}
                        />
                    </div>
                    <div className="relative mb-6">
                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                            <FaLock className="text-gray-500" />
                        </div>
                        <input
                            type="password"
                            id="password"
                            className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-blue-500"
                            placeholder="パスワード"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
                    <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline">
                        ログイン
                    </button>
                </form>
        <div className="mt-4 text-center">
          <Link href="/register" className="text-blue-500 hover:text-blue-700">
                新規登録はこちら
          </Link>
        </div>
            </div>
        </div>
    );
};

export default Login;

import { useState, useEffect, useRef } from 'react';
import { useRouter } from 'next/router';
import { v4 as uuidv4 } from 'uuid';
import { createClient } from '@supabase/supabase-js';
import { FaPaperPlane, FaUser, FaSignOutAlt } from 'react-icons/fa';
import Link from 'next/link';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || '';
const supabase = createClient(supabaseUrl, supabaseKey);

const Chat = () => {
    const [messages, setMessages] = useState<{
        id: string;
        sender: string;
        text: string;
        timestamp: string;
    }[]>([    // Initial dummy data for chat.
        {
            id: uuidv4(),
            sender: 'AI',  // Changed sender to 'AI'
            text: 'こんにちは！発注のご用件をどうぞ。',
            timestamp: new Date().toLocaleTimeString(),
        },
        {
            id: uuidv4(),
            sender: 'AI',
            text: '例：商品A 5個を発注',
            timestamp: new Date().toLocaleTimeString(),
        }
    ]);
    const [inputMessage, setInputMessage] = useState('');
    const chatContainerRef = useRef<HTMLDivElement>(null);
    const [user, setUser] = useState<any>(null);
    const router = useRouter();
    const [error, setError] = useState<string | null>(null);


    useEffect(() => {
        supabase.auth.getSession().then(({ data: { session } }) => {
            if (session) {
                fetchUserData(session.user.id);
                return
            }
            router.push('/login')
        });

        // Scroll to bottom of chat container when messages change
        if (chatContainerRef.current) {
          chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [messages, router]);

    const fetchUserData = async (userId:string)=>{
        const { data, error } = await supabase
        .from('users')
        .select('*')
        .eq('id', userId)
        .single();
      if (error) {
        setError('ユーザー情報の取得に失敗しました');
      } else if (data) {
        setUser(data);
      }
    }

    const handleLogout = async () => {
        const { error } = await supabase.auth.signOut();
        if (error) {
          setError('ログアウトに失敗しました。');
        } else {
          router.push('/login');
        }
      };

    const handleSendMessage = async () => {
        if (inputMessage.trim() === '') return;

        const newMessage = {
            id: uuidv4(),
            sender: 'user',
            text: inputMessage,
            timestamp: new Date().toLocaleTimeString(),
        };

        setMessages([...messages, newMessage]);
        setInputMessage('');
        try {
            const response = await fetch('/api/order-chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ message: inputMessage, userId:user.id }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            const aiResponse = {
              id: uuidv4(),
              sender: 'AI',
              text: data.response,
              timestamp: new Date().toLocaleTimeString(),
          };
          setMessages(prevMessages => [...prevMessages, aiResponse]);


        } catch (error) {
            console.error('There was a problem with the fetch operation:', error);
            const errorMessage = {
                id: uuidv4(),
                sender: 'AI',
                text: '申し訳ございません。エラーが発生しました。',
                timestamp: new Date().toLocaleTimeString(),
            };
            setMessages(prevMessages => [...prevMessages, errorMessage]);
        }
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setInputMessage(e.target.value);
    };

    const handleInputKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
        if (e.key === 'Enter') {
            handleSendMessage();
        }
    };

    return (
      <div className="min-h-screen h-full bg-gray-100 flex">
          <aside className="bg-gray-200 w-64 p-4">
            <div className="flex items-center justify-between mb-4">
              <span className="font-bold text-lg">RECERQA AI</span>
            </div>

              <nav>
                  <ul>
                  <li className="mb-2">
                      <Link href="/chat" className="flex items-center px-4 py-2 text-gray-700 hover:bg-gray-300 rounded">
                            <span className='mr-2'>チャット発注</span>
                        </Link>
                  </li>
                  <li className="mb-2">
                      <Link href="/voice" className="flex items-center px-4 py-2 text-gray-700 hover:bg-gray-300 rounded">
                          <span className='mr-2'>音声発注</span>
                        </Link>
                    </li>
                    <li className="mb-2">
                        <Link href="/ec" className="flex items-center px-4 py-2 text-gray-700 hover:bg-gray-300 rounded">
                            <span className='mr-2'>ECサイト発注</span>
                        </Link>
                    </li>
                    <li className="mb-2">
                          <Link href="/order-history" className="flex items-center px-4 py-2 text-gray-700 hover:bg-gray-300 rounded">
                              <span className='mr-2'>発注履歴</span>
                          </Link>
                      </li>

                </ul>
              </nav>
              <div className="mt-4 pt-4 border-t border-gray-300">
                    <div className='flex items-center justify-between'>
                    <div className='flex items-center '>
                    <FaUser className="text-gray-600 mr-2" />
                    <span className="text-gray-700">{user ? user.customer_name : 'Guest'}</span>
                    </div>
                      <button onClick={handleLogout} className='flex items-center text-gray-700 hover:text-red-500'>
                        <FaSignOutAlt className="mr-1"/>ログアウト
                      </button>
                    </div>
                </div>
          </aside>
        <div className="flex-1 flex flex-col bg-white shadow-md rounded-lg overflow-hidden m-4">
          <div className="bg-gray-100 p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-700">チャット発注画面</h2>
          </div>
        <div ref={chatContainerRef} className="flex-1 p-4 overflow-y-auto space-y-4">
                {messages.map((message) => (
                    <div
                        key={message.id}
                        className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}
                    >
                        <div
                            className={`${message.sender === 'user' ? 'bg-blue-500 text-white rounded-tl-lg rounded-tr-lg rounded-bl-lg' : 'bg-gray-300 text-gray-800 rounded-tl-lg rounded-tr-lg rounded-br-lg'}
                            p-2 max-w-[70%] sm:max-w-[50%] md:max-w-[40%] `}
                        >
                            <p className="text-sm">{message.text}</p>
                            <div className="text-xs text-gray-500 mt-1 text-right">{message.timestamp}</div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="p-4 border-t border-gray-200 flex">
                <input
                    type="text"
                    className="flex-1 px-3 py-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="メッセージを入力してください..."
                    value={inputMessage}
                    onChange={handleInputChange}
                    onKeyDown={handleInputKeyDown}
                />
                <button
                    className="ml-2 bg-blue-500 text-white p-2 rounded hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    onClick={handleSendMessage}
                >
                    <FaPaperPlane />
                </button>
            </div>
            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
                    <span className="block sm:inline">{error}</span>
                </div>
            )}
        </div>
    </div>
    );
};

export default Chat;

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import { createClient } from '@supabase/supabase-js';
import { FaMicrophone, FaStop } from 'react-icons/fa';
import Link from 'next/link';


const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || '';
const supabase = createClient(supabaseUrl, supabaseKey);

const Voice = () => {
  const router = useRouter();
  const [isRecording, setIsRecording] = useState(false);
  const [transcription, setTranscription] = useState('');
  const [audioStream, setAudioStream] = useState<MediaStream | null>(null);
  const [mediaRecorder, setMediaRecorder] = useState<MediaRecorder | null>(null);
  const [audioChunks, setAudioChunks] = useState<BlobPart[]>([]);
  const [orderStatus, setOrderStatus] = useState<string | null>(null);


  useEffect(() => {
    // Check for user authentication on component mount
    const checkAuth = async () => {
        const { data: { session } } = await supabase.auth.getSession();

        if (!session) {
            router.push('/login'); // Redirect to login if not authenticated
        }
    };

      checkAuth();
  }, []);

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            setAudioStream(stream);
            const recorder = new MediaRecorder(stream);
            setMediaRecorder(recorder);
            setAudioChunks([]);

            recorder.ondataavailable = (event) => {
                if (event.data.size > 0) {
                    setAudioChunks((prevChunks) => [...prevChunks, event.data]);
                }
            };

            recorder.onstop = () => {
                const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
                sendAudioForTranscription(audioBlob);
            };

            recorder.start();
            setIsRecording(true);
        } catch (error) {
            console.error('Error starting recording:', error);
            setOrderStatus("音声入力に失敗しました。");
        }
    };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      setIsRecording(false);
      if (audioStream) {
        audioStream.getTracks().forEach((track) => track.stop());
        setAudioStream(null);
      }
    }
  };

 const sendAudioForTranscription = async (audioBlob: Blob) => {
    try {
      setOrderStatus("解析中...");
      const formData = new FormData();
      formData.append('audio', audioBlob, 'voice.webm');

      const response = await fetch('/api/order-voice', {
        method: 'POST',
        body: formData,
      });

        if (!response.ok) {
        const errorData = await response.json(); // Try to parse the JSON error
        console.error('API Error:', errorData);
        setTranscription("音声認識エラーが発生しました。");
        setOrderStatus("音声認識エラーが発生しました。");
         return;
        }

        const data = await response.json();
        setTranscription(data.transcription || "音声認識の結果を取得できませんでした。");
        setOrderStatus("音声認識が完了しました。");

    } catch (error) {
      console.error('Error sending audio:', error);
      setTranscription("通信エラーが発生しました。");
      setOrderStatus("通信エラーが発生しました。");
    }
  };

    const handleLogout = async () => {
        await supabase.auth.signOut();
        router.push('/login');
    };

  return (
    <div className="min-h-screen h-full bg-gray-100 flex">
        <aside className="bg-gray-200 w-64 p-4">
                <nav>
                    <ul>
                         <li className="mb-2">
                            <Link href="/chat" className="block p-2 hover:bg-gray-300 rounded">チャット発注画面</Link>
                         </li>
                        <li className="mb-2">
                            <Link href="/voice" className="block p-2 hover:bg-gray-300 rounded">音声発注画面</Link>
                        </li>
                        <li className="mb-2">
                           <Link href="/ec" className="block p-2 hover:bg-gray-300 rounded">ECサイト発注画面</Link>
                         </li>
                         <li className="mb-2">
                           <button onClick={handleLogout} className="block p-2 hover:bg-gray-300 rounded">ログアウト</button>
                         </li>
                    </ul>
                </nav>
        </aside>
      <div className="flex-1 p-4 flex flex-col items-center">
        <h1 className="text-2xl font-bold mb-6">音声発注画面</h1>
        <div className="flex flex-col items-center mb-6">
          <button
            onClick={isRecording ? stopRecording : startRecording}
            className={`flex items-center justify-center px-6 py-3 rounded-full ${isRecording ? 'bg-red-500 hover:bg-red-700' : 'bg-blue-500 hover:bg-blue-700'} text-white focus:outline-none`}
          >
            {isRecording ? (
              <>
                <FaStop className="mr-2" />停止
              </>
            ) : (
              <>
                <FaMicrophone className="mr-2" />録音開始
              </>
            )}
          </button>
          </div>
             {orderStatus && (
              <p className={`mt-4 text-center ${orderStatus.includes("エラー") ? 'text-red-500' : 'text-green-500'}`}>
                {orderStatus}
            </p>
        )}
        <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
          <h2 className="text-lg font-semibold mb-4">音声入力結果</h2>
          <div className="border p-4 rounded-md min-h-[100px] mb-4">
           {transcription ||  <p className="text-gray-500">ここに音声入力の結果が表示されます。</p>}
          </div>

        </div>
      </div>
    </div>
  );
};

export default Voice;

import React, { useState, useEffect } from 'react';
import { useRouter } from 'next/router';
import Link from 'next/link';
import { createClient } from '@supabase/supabase-js';
import { FaShoppingCart, FaUser, FaHome, FaList } from 'react-icons/fa';

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || '';
const supabase = createClient(supabaseUrl, supabaseKey);

const Ec = () => {
  const router = useRouter();
  const [products, setProducts] = useState<any[]>([]);
  const [cart, setCart] = useState<any[]>([]);
  const [user, setUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
        const fetchSession = async () => {
            const { data: { session } } = await supabase.auth.getSession();
            if (session) {
                const { data: userData, error: userError } = await supabase
                    .from('users')
                    .select('*')
                    .eq('id', session.user.id)
                    .single();

                if (userError) {
                    console.error('ユーザーデータの取得エラー:', userError);
                    setUser(null);
                } else {
                    setUser(userData);
                }
            } else {
                setUser(null);
            }
        };
        fetchSession();
    }, []);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
              const { data, error } = await supabase.from('products').select('product_id, product_name, unit_price');
              if (error) {
                  console.error('Error fetching products:', error);
                  setProducts([
                        {
                            "product_id": "sample-001",
                            "product_name": "サンプル商品1",
                            "unit_price": 1000,
                          },
                          {
                            "product_id": "sample-002",
                            "product_name": "サンプル商品2",
                            "unit_price": 2000
                          },
                            {
                              "product_id": "sample-003",
                              "product_name": "サンプル商品3",
                              "unit_price": 1500,
                            }
                      ]);
              } else {
                setProducts(data);
              }
            } catch (error) {
              console.error('Unexpected error fetching products:', error);
              setProducts([
                {
                    "product_id": "sample-001",
                    "product_name": "サンプル商品1",
                    "unit_price": 1000,
                  },
                  {
                    "product_id": "sample-002",
                    "product_name": "サンプル商品2",
                    "unit_price": 2000
                  },
                    {
                      "product_id": "sample-003",
                      "product_name": "サンプル商品3",
                      "unit_price": 1500,
                    }
            ]);
            }
            finally {
                setLoading(false);
            }
        };

        fetchProducts();
    }, []);

  const addToCart = (product: any) => {
    setCart([...cart, { ...product, quantity: 1 }]);
  };

  const updateQuantity = (index: number, newQuantity: number) => {
    const newCart = [...cart];
    newCart[index].quantity = newQuantity;
    setCart(newCart);
  };

  const removeFromCart = (index: number) => {
    const newCart = cart.filter((_, i) => i !== index);
    setCart(newCart);
  };

    const handleCheckout = async () => {
        if (!user) {
            alert('ログインしてください。');
            return;
        }

        try {
          const orderItems = cart.map(item => ({
              product_id: item.product_id,
              quantity: item.quantity,
              unit_price: item.unit_price
          }));

            const { data: orderData, error: orderError } = await supabase
                .from('orders')
                .insert([{
                  customer_id: user.id,
                  order_date: new Date().toISOString(),
                  order_status: 'pending',
                }])
                .select('order_id')
                .single();

          if(orderError) {
                console.error('Failed to create order:', orderError);
                alert('注文の作成に失敗しました。');
                return;
          }
          if(orderData && orderData.order_id) {
               const { error: orderItemError } = await supabase
                    .from('order_items')
                    .insert(
                      orderItems.map(item => ({
                        order_id: orderData.order_id,
                          product_id: item.product_id,
                        quantity: item.quantity,
                        unit_price: item.unit_price,
                      }))
                    );

              if (orderItemError) {
                    console.error('Failed to create order items:', orderItemError);
                    alert('注文明細の作成に失敗しました。');
                    return;
              } else {
                  alert('注文が完了しました。');
                  setCart([]);
                   router.push('/order-confirmation');
              }
         }
    } catch(error) {
      console.error('Error during checkout:', error);
        alert('注文処理中にエラーが発生しました。');
      }
  };

  if (loading) {
      return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="min-h-screen h-full bg-gray-100">
    <header className="bg-blue-500 p-4 flex justify-between items-center">
        <Link href="/" className="text-white font-bold text-xl flex items-center">
            <FaHome className="mr-2" />ホーム
        </Link>
            <nav className="flex space-x-4">
                <Link href="/order-history" className="text-white hover:text-gray-200 flex items-center">
                    <FaList className="mr-1" />注文履歴
                </Link>
                <Link href="/login" className="text-white hover:text-gray-200 flex items-center">
                <FaUser className="mr-1" />
                    {user ? 'ログアウト' : 'ログイン'}
                </Link>
            </nav>
        </header>
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">商品一覧</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {products.map((product: any) => (
          <div key={product.product_id} className="bg-white shadow-md rounded-lg p-4">
            <img src={`https://placehold.co/300x200?text=${product.product_name}`} alt={product.product_name} className="mb-2 w-full h-40 object-cover rounded"/>
            <h2 className="text-lg font-semibold">{product.product_name}</h2>
            <p className="text-gray-600">価格: ¥{product.unit_price}</p>
            <button onClick={() => addToCart(product)} className="mt-2 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
              カートに追加
            </button>
          </div>
        ))}
      </div>

      <h2 className="text-xl font-bold mt-8 mb-4">カート</h2>
      {cart.length > 0 ? (
        <div className="bg-white shadow-md rounded-lg p-4">
            <ul className="divide-y divide-gray-200">
            {cart.map((item, index) => (
                <li key={index} className="py-2 flex items-center justify-between">
                <div className="flex items-center space-x-4">
                    <img src={`https://placehold.co/100x70?text=${item.product_name}`} alt={item.product_name} className="w-20 h-14 object-cover rounded"/>
                    <div>
                      <p className="font-semibold text-lg">{item.product_name}</p>
                      <p className="text-gray-600">単価: ¥{item.unit_price}</p>
                    </div>
                    </div>
                  <div className="flex items-center space-x-2">
                  <input
                      type="number"
                      className="border border-gray-300 rounded w-16 p-1 text-center"
                      value={item.quantity}
                      min="1"
                      onChange={(e) => updateQuantity(index, parseInt(e.target.value))}
                    />
                    <button onClick={() => removeFromCart(index)} className="text-red-500 hover:text-red-700">
                      削除
                    </button>
                  </div>
                </li>
            ))}
          </ul>
          <div className="mt-4 flex justify-end">
            <button onClick={handleCheckout} className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded">
